import React, { useState } from 'react';
import { Button, SafeAreaView, Text, View,StyleSheet,StatusBar, Image } from 'react-native';
import { borderColor } from 'react-native/Libraries/Components/View/ReactNativeStyleAttributes';

// const [items,setItems]=useState([])

const RenderList=(listItems=[])=>{
    return(
        listItems.listItems.map((item,i)=>{
            return(
                <View style={styles.item_container} key={i}>
                    <View style={styles.item_name}>
                        <Text>{item.name}</Text>
                    </View>
                    <View style={styles.item_quantity}>
                        <Text>{item.quantity}</Text>
                    </View>
                    <View style={styles.item_price}>
                        <Text>{item.price}</Text>
                    </View>
                    <View style={styles.item_done}>
                    </View>
                </View>
            )
        })
    )
}

function ListScreen(props) {
    return (
        <View style={styles.list_container}>
            <View style={styles.bar}>
                <Image style={styles.icon} source={require('../assets/shopit.png')}/>
            </View>
            <RenderList listItems={[{name:'as/v',quantity:11,price:24},{name:'hh',quantity:34,price:245},{name:'hh',quantity:34,price:245}]}/>
            {/* <RenderList listItems={[1,2,3]}/> */}
            {/* {renderList([1,2,3])} */}
        </View>
    );
}

const styles = StyleSheet.create({
    item_container: {
        flexDirection:'row',
        height:'15%',
        justifyContent:'space-around',
        fontWeight:'bold',
        backgroundColor:'#004B86',
        marginBottom:'1%',
        alignItems:'center',
        borderRadius:10
        // borderColor
    },
    list_container:{
        display:'flex',
        justifyContent: 'flex-start',
        marginTop:StatusBar.currentHeight
    },
    item_name:{
        marginLeft:'5%',
        flex:2,
    },
    item_quantity:{
        flex:1,
    },
    item_price:{
        flex:1,
    },
    item_done:{
        flex:1,
        marginRight:'5%',
    },
    bar:{
        alignItems:'center',
        height:'15%'
    },
    icon:{
        height:'10%',
        width:'80%'
    }
})
export default ListScreen;
