import React, { useState } from 'react';
import { Button, SafeAreaView, Text, View,StyleSheet,StatusBar } from 'react-native';

// const [items,setItems]=useState([])

const RenderList=(listItems=[])=>{
    return(
        listItems.listItems.map((item,i)=>{
            return(
                <View style={styles.item_container} key={i}>
                    <View style={styles.item_name}>
                        <Text>{item.name}</Text>
                    </View>
                    <View style={styles.item_quantity}>
                        <Text>{item.quantity}</Text>
                    </View>
                    <View style={styles.item_price}>
                        <Text>{item.price}</Text>
                    </View>
                    <View style={styles.item_edit}>
                        <Button title='Edit'/>
                    </View>
                    <View style={styles.item_done}>
                        <Button title='Done'/>
                    </View>
                </View>
            )
        })
    )
}

function ListScreen(props) {
    return (
        <View style={styles.list_container}>
            {/* <RenderList listItems={[{name:'as/v',quantity:11,price:24},{name:'hh',quantity:34,price:245},{name:'hh',quantity:34,price:245},{name:'hh',quantity:34,price:245},{name:'hh',quantity:34,price:245}]}/> */}
            <RenderList listItems={[{name:'as/v',quantity:11,price:24},{name:'hh',quantity:34,price:245},{name:'hh',quantity:34,price:245}]}/>
            {/* <RenderList listItems={[1,2,3]}/> */}
            {/* {renderList([1,2,3])} */}
        </View>
    );
}

const styles = StyleSheet.create({
    item_container: {
        flexDirection:'row',
        height:'15%',
        color:'#C8FFFA',
        fontWeight:'bold',
        backgroundColor:'#010D2A',
        marginBottom:'2%',
        alignItems:'center',
        borderRadius:20
    },
    list_container:{
        display:'flex',
        justifyContent: 'flex-start',
        marginTop:StatusBar.currentHeight
    },
    item_name:{
        flex:2
    }
})
export default ListScreen;
